import {
	Button,
	Dialog,
	DialogActions,
	Grid,
	Radio,
	DialogContent,
	DialogTitle,
	Paper,
} from "@material-ui/core";
import Autocomplete from "@material-ui/lab/Autocomplete";
import React from "react";
import Draggable from "react-draggable";
import { TextValidator, ValidatorForm } from "react-material-ui-form-validator";
import NotificationPopup from "../Component/NotificationPopup/NotificationPopup";
import { getAllByRoot, getAllRoles, saveItem } from "./MenuService";
import SelectParentPopup from "./SelectParentPopup";
import { Block, Save } from "@material-ui/icons";
function PaperComponent(props) {
	return (
		<Draggable
			handle="#draggable-dialog-title"
			cancel={'[class*="MuiDialogContent-root"]'}
		>
			<Paper {...props} />
		</Draggable>
	);
}

class SupplierDialog extends React.Component {
	constructor(props) {
		super(props);
		this.handleChange = this.handleChange.bind(this);
	}
	state = {
		path: "",
		rowsPerPage: 5,
		page: 0,
		data: [],
		totalElements: 0,
		itemList: [],
		shouldOpenEditorDialog: false,
		shouldOpenConfirmationDialog: false,
		selectedItem: {},
		keyword: "",
		shouldOpenNotificationPopup: false,
		shouldOpenSelectParentPopup: false,
		roles: [],
		listRole: [],
	};
	setPage = page => {
		this.setState({ page }, function () {
			this.updatePageData();
		});
	};
	handleChangeCommonObjectType = event => {
		let { commonObjectTypes } = this.state;
		this.setState({
			type: commonObjectTypes.find(
				item => item.id === event.target.value
			),
			typeId: event.target.value,
		});
	};

	setRowsPerPage = event => {
		this.setState({ rowsPerPage: event.target.value, page: 0 });
		this.updatePageData();
	};

	handleChangePage = (event, newPage) => {
		this.setPage(newPage);
	};
	updatePageData = () => {
		var searchObject = {};
		searchObject.pageIndex = this.state.page;
		searchObject.pageSize = this.state.rowsPerPage;
		getAllByRoot().then(({ data }) => {
			this.setState({
				itemList: [...data.content],
				totalElements: data.totalElements,
			});
		});
	};

	componentDidMount() {
		getAllRoles().then(({ data }) => {
			this.setState({
				listRole: data,
			});
		});
	}

	handleClick = (event, item) => {
		//alert(item);
		if (item.id != null) {
			this.setState({ selectedValue: item.id, selectedItem: item });
		} else {
			this.setState({ selectedValue: item.id, selectedItem: null });
		}
	};
	componentWillMount() {
		let { item } = this.props;
		this.setState({ item: item });
		this.setState({ ...item }, () => {
			let { type } = this.state;
			if (type && type.id) {
				this.setState({ typeId: type.id });
			}
		});
		// getAllRoles().then(({ data }) => {
		//   debugger
		//     this.setState({
		//       listRole: data,
		//     })
		//   })
		//   console.log(this.state.listRole);
	}
	search = keyword => {
		var searchObject = {};
		searchObject.text = keyword;
		searchObject.pageIndex = this.state.page;
		searchObject.pageSize = this.state.rowsPerPage;
		getAllByRoot().then(({ data }) => {
			this.setState({
				itemList: [...data.content],
				totalElements: data.totalElements,
			});
		});
	};

	handleChange(event) {
		this.setState({ keyword: event.target.value });
	}
	handleChangeName = event => {
		this.setState({ name: event.target.value });
	};
	handleChangeCode = event => {
		this.setState({ code: event.target.value });
	};
	handleChangePath = event => {
		this.setState({ path: event.target.value });
	};

	handleChangeValue = event => {
		this.setState({ [event.target.name]: event.target.value });
	};

	openParentPopup = () => {
		this.setState({ shouldOpenSelectParentPopup: true });
	};
	handleSelectParent = parent => {
		//alert(parent.name);
		this.setState({ parent: parent });
		this.setState({ shouldOpenSelectParentPopup: false });
		//this.setState({shouldOpenSelectParentPopup:true});
	};

	handleFormSubmit = () => {
		let { id } = this.state;
		let { code } = this.state;
		//let item = this.state;
		let item = {};
		item.id = this.state.id;
		item.name = this.state.name;
		item.code = this.state.code;
		item.viewIndex = this.state.viewIndex;
		item.icon = this.state.icon;
		item.path = this.state.path;
		item.parent = this.state.parent;
		item.roles = this.state.roles;
		item.description = this.state.description;
		// item.roles=[];
		// if(this.state.roles!=null){
		//   this.state.roles.forEach(e=>{
		//     var menuRole ={};
		//     menuRole.role={};
		//     menuRole.role.id = e.id;
		//     item.roles.push(menuRole);
		//   })
		// }
		console.log(item);
		saveItem(item).then(() => {
			this.props.handleOKEditClose();
		});
	};
	handleDialogClose = () => {
		this.setState({
			shouldOpenNotificationPopup: false,
			shouldOpenSelectParentPopup: false,
		});
	};

	selectRoles = rolesSelected => {
		this.setState({ roles: rolesSelected }, function () {});
	};

	render() {
		const { t, i18n, open } = this.props;
		let {
			name,
			code,
			icon,
			path,
			shouldOpenNotificationPopup,
			listRole,
			roles,
			description,
		} = this.state;
		return (
			<Dialog
				open={open}
				PaperComponent={PaperComponent}
				maxWidth="sm"
				fullWidth
			>
				{shouldOpenNotificationPopup && (
					<NotificationPopup
						title={t("general.noti")}
						open={shouldOpenNotificationPopup}
						// onConfirmDialogClose={this.handleDialogClose}
						onYesClick={this.handleDialogClose}
						text={t(this.state.Notification)}
						agree={t("general.agree")}
					/>
				)}
				<DialogTitle
					style={{ cursor: "move" }}
					id="draggable-dialog-title"
				>
					{t("general.saveUpdate")}
				</DialogTitle>
				<ValidatorForm ref="form" onSubmit={this.handleFormSubmit}>
					<DialogContent dividers>
						<Grid container spacing={2}>
							<Grid item sm={6} xs={12}>
								<TextValidator
									className="w-100"
									label={
										<span>
											<span style={{ color: "red" }}>
												{" "}
												*
											</span>
											{t("menu.key")}
										</span>
									}
									onChange={this.handleChangeValue}
									type="text"
									name="name"
									value={name}
									validators={["required"]}
									errorMessages={[t("general.required")]}
								/>
							</Grid>
							<Grid item sm={6} xs={12}>
								<Button
									size="small"
									style={{
										position: "absolute",
										cursor: "pointer",
										right: "24px",
										zIndex: "9999",
										marginTop: "12px",
									}}
									variant="contained"
									color="primary"
									onClick={this.openParentPopup}
								>
									{t("general.select")}
								</Button>
								<TextValidator
									size="small"
									fullWidth
									InputProps={{
										readOnly: true,
									}}
									label={
										<span>
											<span
												style={{ color: "red" }}
											></span>
											{t("menu.parent")}
										</span>
									}
									value={
										this.state.parent != null
											? this.state.parent.name
											: ""
									}
								/>

								{this.state.shouldOpenSelectParentPopup && (
									<SelectParentPopup
										open={
											this.state
												.shouldOpenSelectParentPopup
										}
										handleSelect={this.handleSelectParent}
										selectedItem={
											this.state.item != null &&
											this.state.item.parent != null
												? this.state.item.parent
												: {}
										}
										handleClose={this.handleDialogClose}
										t={t}
										i18n={i18n}
									/>
								)}
							</Grid>

							<Grid item sm={6} xs={12}>
								<TextValidator
									className="w-100 "
									label={
										<span>
											<span style={{ color: "red" }}>
												{" "}
												*
											</span>

											{t("menu.code")}
										</span>
									}
									onChange={this.handleChangeValue}
									type="text"
									name="code"
									value={code}
									validators={["required"]}
									errorMessages={[t("general.required")]}
								/>
							</Grid>
							<Grid item sm={6} xs={12}>
								<TextValidator
									className="w-100 "
									label={
										<span>
											<span style={{ color: "red" }}>
												{" "}
												*
											</span>

											{t("menu.icon")}
										</span>
									}
									onChange={this.handleChangeValue}
									type="text"
									name="icon"
									value={icon}
									validators={["required"]}
									errorMessages={[t("general.required")]}
								/>
							</Grid>
							<Grid item sm={6} xs={12}>
								<TextValidator
									className="w-100"
									label={
										<span>
											<span style={{ color: "red" }}>
												{" "}
												*
											</span>

											{t("menu.path")}
										</span>
									}
									onChange={this.handleChangeValue}
									type="text"
									name="path"
									value={path}
									validators={["required"]}
									errorMessages={[t("general.required")]}
								/>
							</Grid>
							<Grid item sm={6} xs={12}>
								<TextValidator
									className="w-100 "
									label={
										<span>
											<span style={{ color: "red" }}>
												{" "}
												*
											</span>

											{t("menu.description")}
										</span>
									}
									onChange={this.handleChangeValue}
									type="text"
									name="description"
									value={description}
									validators={["required"]}
									errorMessages={[t("general.required")]}
								/>
							</Grid>
							<Grid item sm={12} xs={12}>
								{listRole && (
									<Autocomplete
										style={{ width: "100%" }}
										multiple
										id="combo-box-demo"
										defaultValue={roles}
										options={listRole}
										getOptionSelected={(option, value) =>
											option.id === value.id
										}
										getOptionLabel={option =>
											option.authority
										}
										onChange={(event, value) => {
											this.selectRoles(value);
										}}
										renderInput={params => (
											<TextValidator
												{...params}
												value={roles}
												// label={t('user.role')}
												label={
													<span>
														<span
															style={{
																color: "red",
															}}
														>
															*
														</span>
														{t("user.role")}
													</span>
												}
												fullWidth
												validators={["required"]}
												errorMessages={[
													t(
														"user.please_select_permission"
													),
												]}
											/>
										)}
									/>
								)}
							</Grid>
						</Grid>
					</DialogContent>

					<DialogActions>
						<div className="flex flex-space-between flex-middle">
							<Button
								variant="contained"
								className="mr-12"
								startIcon={<Block />}
								color="secondary"
								onClick={() => this.props.handleClose()}
							>
								{t("general.cancel")}
							</Button>
							<Button
								variant="contained"
								color="primary"
								className="mr-8"
								startIcon={<Save />}
								type="submit"
							>
								{t("general.save")}
							</Button>
						</div>
					</DialogActions>
				</ValidatorForm>
			</Dialog>
		);
	}
}
export default SupplierDialog;
